mapper.select:
SELECT LINK, PATH, datetime(DATE, 'localtime') as DATE, METHOD, AUTH
	FROM SHARE
WHERE LINK = #{link}

SELECT LINK, PATH, datetime(DATE, 'localtime') as DATE, METHOD, AUTH
	FROM SHARE
WHERE PATH = #{path}

SELECT sg.SHARE_LINK, sg.GROUP_NO, gs.NAME as GROUP_NAME, sg.AUTH
	FROM SHARE_GROUP sg, GROUP_SETTING gs


			sg.SHARE_LINK = #{shareLink}


			AND sg.GROUP_NO = #{groupNo}


			AND sg.GROUP_NO = gs.NO
